import axios from 'axios'
import { resolveImageUrl } from '@/utils'

export function getShopList({ currentPage, pageSize }) {
  return axios.get('/api/shop/recommend', {
    params: {
      currentPage,
      pageSize,
    },
  }).then(({ data }) => {
    if (data.code !== 0) throw new Error('获取数据失败')
    const result = data.data.map(({ restaurant, foods }) => {
      return {
        id: restaurant.id,
        name: restaurant.name,
        rating: restaurant.rating,
        recentOrderNum: restaurant.recent_order_num,
        floatMinimumOrderAmount: restaurant.float_minimum_order_amount,
        floatDeliveryFee: restaurant.float_delivery_fee,
        distance: restaurant.distance,
        orderLeadTime: restaurant.order_lead_time,
        recommendReasons: restaurant.recommend_reasons.map(({ reason }) => reason),
        supportTags: restaurant.support_tags.map(item => ({
          color: item.color,
          border: item.border,
          text: item.text,
          background: item.background ? {
            rgbFrom: item.background.rgb_from,
            rgbTo: item.background.rgb_to,
          } : null,
        })),
        imagePath: resolveImageUrl(restaurant.image_path),
        activities: restaurant.activities.map(item => ({
          description: item.description,
          iconColor: item.icon_color,
          iconName: item.icon_name,
          id: item.id,
        })),
        foods: foods.map((food) => ({
          id: food.id,
          name: food.name,
          price: food.price,
          imagePath: resolveImageUrl(food.image_path),
        })),
      }
    })
    return result
  })
}

function topMenu(menu, restaurant) {
  restaurant.menu.unshift({
    id: menu.id,
    name: menu.name,
    iconUrl: menu.icon_url ? resolveImageUrl(menu.icon_url) : '',
    count: 0,
    foods: menu.foods.map((food) => {
      const m = restaurant.menu.find(item => item.id === food.category_id)
      return m.foods.find(item => item.id === food.item_id)
    }),
  })
}

export function getRestaurant(id) {
  return axios.get('/api/shop/detail', {
    params: {
      id,
    },
  }).then(({ data }) => {
    if (data.code !== 0) throw new Error('获取数据失败')
    const { rst, redpack, menu } = data.data
    const hot = -1
    const cheap = -2
    let hotMenu = null
    let cheapMenu = null
    let index = menu.findIndex(item => item.id === hot)
    if (index !== -1) hotMenu = menu.splice(index, 1)[0]
    index = menu.findIndex(item => item.id === cheap)
    if (index !== -1) cheapMenu = menu.splice(index, 1)[0]
    const restaurant = {
      id: rst.id,
      name: rst.name,
      rating: rst.rating, // 评分
      floatDeliveryFee: rst.float_delivery_fee, // 配送费
      floatMinimumOrderAmount: rst.float_minimum_order_amount, // 最低起送价
      recentOrderNum: rst.recent_order_num, // 月销量
      orderLeadTime: rst.order_lead_time, // 配送时间
      notice: rst.promotion_info, // 公告
      imagePath: resolveImageUrl(rst.image_path),
      bgImagePath: resolveImageUrl(rst.shop_sign.image_hash),
      redpack: redpack.map(item => ({
        value: item.value,
        title: item.title,
      })),
      tags: rst.activity_tags.map(item => ({
        border: item.border,
        color: item.color,
        text: item.text,
      })),
      activities: rst.activities.map(item => ({
        id: item.id,
        border: item.border,
        color: item.text_color,
        description: item.description,
        iconName: item.icon_name,
      })),
      supports: rst.supports.map(item => ({
        id: item.id,
        border: item.border,
        description: item.description,
        color: item.text_color,
        name: item.name,
      })),
      menu: menu.map(item => ({
        id: item.id,
        name: item.name,
        iconUrl: item.icon_url ? resolveImageUrl(item.icon_url) : '',
        count: 0, // 该菜单选购的商品数量
        foods: item.foods.map(food => ({
          id: food.item_id,
          categoryId: food.category_id,
          name: food.name,
          sell: food.month_sales,
          originPrice: food.origin_price,
          price: food.price,
          rating: food.satisfy_rate,
          description: food.description.trim(),
          discountRate: food.discount_rate, // 折扣
          imagePath: resolveImageUrl(food.image_path),
          count: 0, // 该商品选购数量
        })),
      })),
      shopCar: [],
    }
    if (hotMenu) topMenu(hotMenu, restaurant)
    if (cheapMenu) topMenu(cheapMenu, restaurant)
    return restaurant
  })
}

import axios from 'axios'
import { Notify } from '@/ui'
import { getShopList, getRestaurant } from '@/store/api'
import { resolveImageUrl } from '@/utils'
import {
  GET_SHOPLIST_DATA,
  GET_FILTER_DATA,
  GET_RESTAURANT_DATA,
} from './action-types'
import {
  SAVE_FILTER_DATA,
  SAVE_RESTAURANT_DATA,
  SAVE_FOOD_DATA,
  INCREASE,
  DECREASE,
  CLEAR_SHOPCAR_DATA,
  SAVE_SHOPLIST_DATA,
  CLEAR_SHOPLIST_DATA,
  DECREASE_SHOPLIST_DATA,
} from './mutation-types'

const actions = {
  [GET_SHOPLIST_DATA]({ commit, state: { shopList } }) {
    return new Promise((resolve) => {
      if (shopList.finish) return resolve()
      getShopList({
        currentPage: shopList.currentPage,
        pageSize: shopList.pageSize,
      }).then((data) => {
        commit(SAVE_SHOPLIST_DATA, data)
        resolve()
      }).catch(() => {
        Notify({ type: 'danger', message: '获取数据失败' })
      })
    })
  },
  [GET_FILTER_DATA]({ commit }) {
    return axios.get('/api/shop/sort')
      .then(({ data }) => {
        if (data.code !== 0) throw new Error('获取数据失败')
        const { sortList, filterList } = data.data
        commit(SAVE_FILTER_DATA, {
          insideSortFilter: sortList.insideSortFilter,
          outsideSortFilter: sortList.outsideSortFilter,
          outsideFilters: sortList.outsideFilters,
          supports: filterList.supports.map(support => ({
            id: support.id,
            name: support.name,
            imagePath: resolveImageUrl(support.iconHash),
          })),
          activities: filterList.activityTypes,
          averageCosts: filterList.averageCosts,
        })
      })
  },
  [GET_RESTAURANT_DATA]({ commit }, id) {
    return new Promise((resolve, reject) => {
      getRestaurant(id)
        .then((data) => {
          commit(SAVE_RESTAURANT_DATA, data)
          resolve()
        })
        .catch(reject)
    })
  },
}

const mutations = {
  [SAVE_FILTER_DATA]({ filter }, payload) {
    filter.initial = true
    for (const key in payload) {
      if (filter.hasOwnProperty(key)) {
        filter[key] = payload[key]
      }
    }
  },
  [SAVE_RESTAURANT_DATA](state, payload) {
    state.restaurant = payload
  },
  [SAVE_FOOD_DATA](state, payload) {
    state.food = payload
  },
  [INCREASE]({ restaurant: { menu, shopCar } }, { id, categoryId }) {
    const item = menu.find(item => item.id === categoryId)
    item.count += 1
    const food = item.foods.find(item => item.id === id)
    food.count += 1
    const index = shopCar.findIndex(item => item === food)
    if (index === -1) shopCar.push(food)
  },
  [DECREASE]({ restaurant: { menu, shopCar } }, { id, categoryId }) {
    const item = menu.find(item => item.id === categoryId)
    item.count -= 1
    const food = item.foods.find(item => item.id === id)
    food.count -= 1
    if (food.count === 0) shopCar.splice(shopCar.findIndex(item => item === food), 1)
  },
  [CLEAR_SHOPCAR_DATA]({ restaurant: { menu, shopCar } }) {
    menu.forEach((item) => {
      item.count = 0
    })
    shopCar.forEach((item) => {
      item.count = 0
    })
    shopCar.length = 0
  },
  [SAVE_SHOPLIST_DATA]({ shopList }, data) {
    if (data.length === 0 || data.length < shopList.pageSize) shopList.finish = true
    shopList.currentPage += 1
    shopList.data.push(...data)
  },
  [CLEAR_SHOPLIST_DATA]({ shopList }) {
    shopList.data = []
    shopList.finish = false
    shopList.currentPage = 1
  },
  [DECREASE_SHOPLIST_DATA]({ shopList }, index) {
    shopList.data.splice(index, 1)
  },
}

export default {
  namespaced: true,
  state: () => ({
    filter: {
      initial: false,
      insideSortFilter: [],
      outsideSortFilter: [],
      outsideFilters: [],
      supports: [],
      activities: [],
      averageCosts: [],
    },
    restaurant: null,
    food: null,
    shopList: {
      data: [],
      finish: false,
      currentPage: 1,
      pageSize: 8,
    },
  }),
  actions,
  mutations,
}

import axios from 'axios'
import { getShopList } from '@/store/api'

import {
  GET_ADVERTISEMENT_DATA,
  GET_KIND_DATA,
  GET_SHOPLIST_DATA,
} from './action-types'

import {
  SAVE_ADVERTISEMENT_DATA,
  SAVE_KIND_DATA,
  SAVE_SHOPLIST_DATA,
  UPDATE_SHOPLIST_DATA,
  DELETE_SHOPLIST_DATA,
} from './mutation-types'

const actions = {
  [GET_ADVERTISEMENT_DATA]({ commit }) {
    return axios.get('/api/advertisement/list')
      .then(({ data }) => {
        if (data.code !== 0) throw new Error('获取数据失败')
        commit(SAVE_ADVERTISEMENT_DATA, data.data)
      })
  },
  [GET_KIND_DATA]({ commit }) {
    return axios.get('/api/kind/list')
      .then(({ data }) => {
        if (data.code !== 0) throw new Error('获取数据失败')
        commit(SAVE_KIND_DATA, data.data)
      })
  },
  [GET_SHOPLIST_DATA]({ commit, state: { shopList } }, more) {
    return new Promise((resolve, reject) => {
      if (shopList.finish) return resolve()
      getShopList({
        currentPage: shopList.currentPage,
        pageSize: shopList.pageSize,
      }).then((data) => {
        commit(SAVE_SHOPLIST_DATA, { data, more })
        resolve()
      }).catch(reject)
    })
  },
}

const mutations = {
  [SAVE_ADVERTISEMENT_DATA](state, data) {
    state.advertisement = data
  },
  [SAVE_KIND_DATA](state, data) {
    const length = Math.ceil(data.length / state.col)
    for (let i = 0; i < length; i += 1) {
      state.kindGroup.push(data.slice(i * state.col, (i + 1) * state.col))
    }
  },
  [SAVE_SHOPLIST_DATA]({ shopList }, { data, more }) {
    if (data.length === 0 || data.length < shopList.pageSize) shopList.finish = true
    shopList.currentPage += 1
    if (more) shopList.data.push(...data)
    else shopList.data = data
  },
  [UPDATE_SHOPLIST_DATA]({ shopList }) {
    shopList.finish = false
    shopList.currentPage = 1
  },
  [DELETE_SHOPLIST_DATA]({ shopList }, index) {
    shopList.data.splice(index, 1)
  },
}

// 注意这里使用函数方式创建state
// 否则采用服务端渲染时数据会被共享，那这样是不对，每个请求应该有自己的state
// 一句话，state保证是纯函数
export default {
  namespaced: true,
  state: () => ({
    advertisement: [],
    kindGroup: [],
    shopList: {
      data: [],
      currentPage: 1,
      pageSize: 8,
      finish: false,
    },
    col: 10,
  }),
  actions,
  mutations,
}

